import React from 'react'
import { useDispatchCart, useCart } from '../Components/ContextReducer';
//import { Delete } from '@material-ui/icons';



export default function Cart() {
    let data = useCart();
    let dispatch = useDispatchCart();
    if (data.length === 0) {
        return (
            <div className='m-5 w-100 text-center fs-3'>
                The Cart is Empty !!
            </div>
        )
    }
    let totalPrice = data.reduce((total, food) => total + food.price, 0)
    return (
        <div>
            <div className='container m-auto mt-5 table-responsive table-responsive-sm table-responsive-md table-responsive-lg '>
                <table className='table table-hover'>
                    <thead className='text-success fs-4  '>
                        <tr>
                            <th scope='col'>#</th>
                            <th scope='col'>Name </th>
                            <th scope='col'>Quantity</th>
                            <th scope='col'>Option</th>
                            <th scope='col'>Amount</th>
                            <th scope='col'></th>
                        </tr>


                    </thead>
                    <tbody>
                        {data.map((food, index) => (
                                //{console.log(food.name)}

                            <tr>
                                <th scope='row'>{index+1}</th>
                                <td >{food.name} </td>
                                <td >{food.qty} </td>
                                <td >{food.size} </td>
                                <td >{food.price} </td>
                                <td ><button type="button" className="btn p-0"><Delete onClick={() => { dispatch({ type: "REMOVE", index: index }) }} /></button></td>
                                
                            </tr>

                        ))}
                    </tbody>

                </table>

                <div><h1 className='fs-2'>Total Price : {totalPrice}/-</h1></div>
                <div>
                    <button className='btn btn-success mt-5'>Checkout</button>
                </div>


            </div>
        </div>
    )
}
