import React, { useState } from 'react'
import { useDispatchCart , useCart} from './ContextReducer';

export default function Card(props) {
    let data = u
    let dispatch = useDispatchCart();
    let options = props.options;
    let  priceOptions= Object.keys(options);
    const [qty,setQty]=useState(1);
    const [size,setSize]=useState("");
    const handleAddToCart = async()=>{
        await dispatch({type: "ADD",id:props.foodItem._id, name:props.foodItem.name, price:props.foodItem.price, qty: qty, size : size})
        console.log();
    }

    return (
        <div>
            <div>
                <div className="card mt-3" style={{ "width": "18rem", "maxHeight": "400px" }}>
                    <img src={props.foodItem.imgSrc} className="card-img-top" alt="..." style={{height: "150px", objectFit:"fill"}} />
                    <div className="card-body">
                        <h5 className="card-title">{props.foodItem.foodName}</h5>
                        <p className="card-text">This is some important text</p>
                        <div className='container w-100'>
                            <select className='m-2 h-100 bg-success rounded' onChange={(e)=>setQty(e.target.value)}>
                                {
                                    Array.from(Array(6), (e, i) => {
                                        return (
                                            <option key={i + 1} value={i + 1}>
                                                {i + 1}

                                            </option>
                                        )
                                    })
                                }
                            </select>
                            <select className='m-2 h-100 bg-success rounded'onChange={(e)=>setSize(e.target.value)} >
                                {priceOptions.map((data)=>{
                                    return <option key={data} value={data}> {data}</option>
                                })}
                            </select>
                            <div className='d-inline h-100 fs-5'>
                                Total Price
                            </div>

                        </div>
                        <hr/>
                        <button className='btn btn-success justify-center ms-3' onClick={handleAddToCart}>Add To Cart</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
